import { useState, useEffect } from 'react'
import { Link, useNavigate } from 'react-router-dom'
import axios from 'axios';

function Products() {
    const [products, setProducts] = useState([]);
    const [categories] = useState(['All', 'industry workers', 'medical staff', 'hotel staff', 'Accessories']);
    const [selectedCategory, setSelectedCategory] = useState('All');
    const [search, setSearch] = useState('');
    const navigator = useNavigate();

    // Fetch products by category or all
    const getProducts = () => {
        let url = '';
        if (selectedCategory === 'All') {
            url = 'http://localhost:5000/products';
        } else {
            url = `http://localhost:5000/products/category/${selectedCategory}`;
        }
        axios
            .get(url)
            .then((response) => {
                setProducts(response.data.products);
            })
            .catch((err) => {
                console.log(err);
                setProducts([]);
            });
    };

    // Handle add to cart operation
    function handleAddCart(e) {
        if (!localStorage.getItem('userToken')) {
            navigator('/login');
        } else {
            const token = localStorage.getItem('userToken');
            axios
                .post('http://localhost:5000/cart/add', {
                    p_id: e.target.dataset.id,
                    qty: 1
                }, {
                    headers: {
                        Authorization: token
                    }
                })
                .then(() => {
                    window.alert("Item added to cart");
                })
                .catch((error) => {
                    alert(error.response?.data?.message || "Error adding to cart");
                });
        }
    }

    // Fetch products on mount and when category changes
    useEffect(() => {
        getProducts();
        // eslint-disable-next-line
    }, [selectedCategory]);

    // Filter products by search term
    const filteredProducts = products.filter(product =>
        product.p_name.toLowerCase().includes(search.toLowerCase()) ||
        product.desc.toLowerCase().includes(search.toLowerCase())
    );

    return (
        <>
            {/* --- CATEGORY FILTER --- */}
            <div className="mb-4 text-center">
                {categories.map(cat => (
                    <button
                        key={cat}
                        className={`btn btn-outline-primary mx-1 ${selectedCategory === cat ? 'active' : ''}`}
                        onClick={() => setSelectedCategory(cat)}
                        type="button"
                    >
                        {cat}
                    </button>
                ))}
            </div>
            {/* --- SEARCH BAR --- */}
            <div className="mb-4 text-center">
                <input
                    type="text"
                    className="form-control w-50 d-inline"
                    style={{ maxWidth: 400 }}
                    placeholder="Search products..."
                    value={search}
                    onChange={e => setSearch(e.target.value)}
                />
            </div>
            {/* --- END SEARCH BAR --- */}
            <div className="container-fluid bg-light p-4 shadow mt-4">
                <h3 className="text-center">New Arrivals</h3>
                <hr />
                <div className="row flex-jcenter">
                    {filteredProducts.length === 0 ? (
                        <h1 className="fw-normal">Ooops! Something went Wrong.</h1>
                    ) : filteredProducts.slice(0, 4).map((product) => (
                        <div className="col-md-3 flex-center" key={product._id}>
                            <div className="card">
                                <img src={`http://localhost:5000${product.img[0]}`} className="card-img-top" alt="..." />
                                <div className="card-body">
                                    <h5 className="card-title">{product.p_name}</h5>
                                    <p className="card-text">{product.desc}</p>
                                    <div className="d-flex gap-3">
                                        <Link to={`/products/${product._id}`} className="btn btn-primary">Details</Link>
                                        <button className="btn btn-warning" data-id={product._id} onClick={handleAddCart}>Add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            <div className="container-fluid bg-light p-4 shadow mt-4">
                <h3 className="text-center">Popular Products</h3>
                <hr />
                <div className="row flex-jcenter">
                    {filteredProducts.length === 0 ? (
                        <h1 className="fw-normal">Ooops! Something went Wrong.</h1>
                    ) : filteredProducts.slice(4, 8).map((product) => (
                        <div className="col-md-3 flex-center" key={product._id}>
                            <div className="card">
                                <img src={`http://localhost:5000${product.img[0]}`} className="card-img-top" alt="..." />
                                <div className="card-body">
                                    <h5 className="card-title">{product.p_name}</h5>
                                    <p className="card-text">{product.desc}</p>
                                    <div className="d-flex gap-3">
                                        <Link to={`/products/${product._id}`} className="btn btn-primary">Details</Link>
                                        <button className="btn btn-warning" data-id={product._id} onClick={handleAddCart}>Add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>

            <div className="container-fluid bg-light p-4 shadow mt-4">
                <h3 className="text-center">Our Choice</h3>
                <hr />
                <div className="row flex-jcenter">
                    {filteredProducts.length === 0 ? (
                        <h1 className="fw-normal">Ooops! Something went Wrong.</h1>
                    ) : filteredProducts.slice(8, 45).map((product) => (
                        <div className="col-md-3 flex-center" key={product._id}>
                            <div className="card">
                                <img src={`http://localhost:5000${product.img[0]}`} className="card-img-top" alt="..." />
                                <div className="card-body">
                                    <h5 className="card-title">{product.p_name}</h5>
                                    <p className="card-text">{product.desc}</p>
                                    <div className="d-flex gap-3">
                                        <Link to={`/products/${product._id}`} className="btn btn-primary">Details</Link>
                                        <button className="btn btn-warning" data-id={product._id} onClick={handleAddCart}>Add to Cart</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default Products;
